mapper.select:
SELECT 

FROM t_graduate a

WHERE a.id = #{id}

SELECT 

FROM t_graduate a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.stu_no = #{stuNo}


		AND a.stu_name LIKE 
			'%'||#{stuName}||'%'
			'%'+#{stuName}+'%'
			concat('%',#{stuName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_graduate a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_graduate SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT count(*) from
t_graduate
where stu_no=#{stuNo}
