mapper.select:
SELECT 

FROM t_graduate a

WHERE a.id = #{id}

SELECT

FROM t_graduate a

WHERE a.stu_no = #{stuNo} and a.del_flag = #{DEL_FLAG_NORMAL}

SELECT
FROM t_graduate a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.stu_no LIKE '%'||#{stuNo}||'%' '%'+#{stuNo}+'%' concat('%',#{stuNo},'%')
AND a.org_id = #{orgId}
AND a.session = #{session}
AND a.stu_name LIKE '%'||#{stuName}||'%' '%'+#{stuName}+'%' concat('%',#{stuName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_graduate a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
