mapper.select:
SELECT

FROM t_major a

WHERE a.id = #{id}

SELECT 

FROM t_major a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.major_no = #{majorNo}


		AND a.major_name LIKE 
			'%'||#{majorName}||'%'
			'%'+#{majorName}+'%'
			concat('%',#{majorName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_major a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_major SET 	
	major_no = #{majorNo},
	major_name = #{majorName},
	org_id = #{orgId},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE t_major SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
